<?php\n\nfunction posts() {\n    echo "Viewing posts";\n}\n\n$allowedFunctions = ['posts']; // List of allowed function names\n\n// Get the URI and remove leading/trailing slashes\n$uri = trim($_SERVER['REQUEST_URI'], '/');\n\n// Split the URI into parts\n$uriParts = explode('/', $uri);\n\n// Assume the last part is the function name\n$functionName = end($uriParts);\n\nif (in_array($functionName, $allowedFunctions)) {\n    // Call the function if it's in the allowed list\n    call_user_func($functionName);\n} else {\n    echo "404: Function not found";\n}\n\n?>