<?php\n\nclass My_Controller_Action extends Zend_Controller_Action\n{\n    public function __call($method, $args)\n    {\n        if ('Action' == substr($method, -6)) {\n            // Extract the action name.\n            $action = substr($method, 0, strlen($method) - 6);\n            \n            // Handle the non-existent action in a custom way.\n            $this->handleUnknownAction($action);\n            \n            return;\n        }\n\n        // If we don't handle it here, call parent to throw an exception.\n        parent::__call($method, $args);\n    }\n\n    protected function handleUnknownAction($action)\n    {\n        // Custom logic for handling unknown actions\n        switch ($this->_request->getControllerName()) {\n            case 'user':\n                $this->_forward('not-found', 'error', null, array('controller' => 'user', 'action' => $action));\n                break;\n            case 'admin':\n                $this->_forward('not-authenticated', 'login');\n                break;\n            default:\n                // Default handling\n                parent::__call($action . 'Action', array());\n        }\n    }\n}