function connectToDatabase($dbType = 'mysql', $host, $dbname, $user, $pass) {\n    try {\n        switch($dbType) {\n            case 'mysql':\n                $dsn = "mysql:host=$host;dbname=$dbname;charset=utf8mb4";\n                break;\n            case 'pgsql':\n                $dsn = "pgsql:host=$host;port=5432;dbname=$dbname;";\n                break;\n            case 'sqlite':\n                $dsn = "sqlite:$dbname.db"; // dbname should be the path to your db file\n                break;\n            default:\n                throw new Exception("Unsupported PDO driver specified: $dbType");\n        }\n        \n        $pdo = new PDO($dsn, $user, $pass);\n        $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n        return $pdo;\n    } catch (PDOException $e) {\n        echo "Connection error: " . $e->getMessage();\n    }\n}\n\n// Usage\ntry {\n    $db = connectToDatabase("mysql", "your_host", "your_dbname", "your_user", "your_pass");\n} catch (Exception $e) {\n    echo 'Error: ' . $e->getMessage();\n}