<?php\n   $socket = fsockopen("127.0.0.1", "5038", $errno, $errstr);\n   if (!$socket) {\n       echo "$errstr ($errno)<br>\n";\n   } else {\n       fwrite($socket, "Action: Login\r\n");\n       fwrite($socket, "Username: admin\r\n");\n       fwrite($socket, "Secret: yourpassword\r\n");\n       fwrite($socket, "\r\n");\n\n       // Originate a call example\n       fwrite($socket, "Action: Originate\r\n");\n       fwrite($socket, "Channel: SIP/1000\r\n");\n       fwrite($socket, "Context: default\r\n");\n       fwrite($socket, "Exten: 2000\r\n");\n       fwrite($socket, "Priority: 1\r\n");\n       fwrite($socket, "\r\n");\n\n       while (!feof($socket)) {\n           echo fgets($socket, 4096);\n       }\n\n       fclose($socket);\n   }\n   ?>