// Key should be securely stored, e.g., environment variable.\n$key = random_bytes(SODIUM_CRYPTO_SECRETBOX_KEYBYTES);\n\n$phone_number = "1234567890";\n\n// Create a nonce (number used once) for the encryption\n$nonce = random_bytes(SODIUM_CRYPTO_SECRETBOX_NONCEBYTES);\n\n$encrypted_phone_number = sodium_crypto_secretbox($phone_number, $nonce, $key);\necho "Encrypted: " . base64_encode($encrypted_phone_number) . PHP_EOL;\n\n// Store the nonce along with the encrypted data as it is required for decryption\n$decrypted_phone_number = sodium_crypto_secretbox_open(\n    $encrypted_phone_number,\n    $nonce,\n    $key\n);\n\nif ($decrypted_phone_number === false) {\n    echo "Failed to decrypt.";\n} else {\n    echo "Decrypted: " . $decrypted_phone_number;\n}