$alttitle = "35:title1,36:title2, 59:title5";\n$replacetitles = explode(",", $alttitle);\n$result = array();\n\nforeach ($replacetitles as $pair) {\n    // Remove any whitespace around the pair\n    $pair = trim($pair);\n    \n    // Split the pair into key and value by ":"\n    list($key, $value) = explode(":", $pair, 2);\n\n    // Trim any whitespace from key and value\n    $result[trim($key)] = trim($value);\n}\n\nprint_r($result);