<?php\n$html = file_get_contents('http://miurl.com/mipagina#0');\n// create document object model\n$dom = new DOMDocument();\n// suppress errors due to malformed HTML\n@$dom->loadHTML($html);\n// create domxpath instance\n$xPath = new DOMXPath($dom);\n// get all elements with a particular class and then loop through and print the href attribute\n$elements = $xPath->query("//*[@class='nombrecomplejo']");\nif ($elements != null) {\n    foreach ($elements as $e) {\n        // Check if $e is an anchor tag\n        if ($e instanceof DOMElement && strtolower($e->tagName) === 'a') {\n            // Print the href attribute\n            echo htmlspecialchars($e->getAttribute('href')) . "\n";\n        }\n    } \n}\n?>