<?php\n   $xmlFile = 'http://someexternaldomain.com/data.xml'; // URL of the external XML file\n   $xslFile = 'tabledisplay.xsl';\n\n   // Load the XML document\n   $xmlDoc = new DOMDocument();\n   $xmlDoc->load($xmlFile);\n\n   // Load the XSLT\n   $xsltDoc = new DomDocument();\n   $xsltDoc->load($xslFile);\n\n   // Configure the transformer and perform the transformation\n   $proc = new XSLTProcessor();\n   $proc->importStyleSheet($xsltDoc);\n   echo $proc->transformToXML($xmlDoc);\n   ?>