<?php\n// Database connection parameters\n$host = 'hostname';\n$dbname = 'm118';\n$username = 'username';\n$password = 'password';\n\ntry {\n    // Create a new PDO instance to connect to the MySQL database\n    $pdo = new PDO("mysql:host=$host;dbname=$dbname", $username, $password);\n    // Set the PDO error mode to exception\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Open the CSV file for reading\n    if (($handle = fopen('path/to/yourfile.csv', 'r')) === FALSE) {\n        die('Error opening CSV file');\n    }\n\n    // Read the header row\n    fgetcsv($handle); // Assuming headers id, sku are in first line\n\n    // Loop through each row of the CSV file and process it\n    for ($i = 0; $i < 1486; $i++) { // Adjust this if you have more or less lines\n        $data = fgetcsv($handle);\n\n        if ($data !== FALSE) {\n            list($id, $sku) = $data;\n\n            // Prepare and execute the SQL query\n            $stmt = $pdo->prepare("UPDATE catalog_product_entity SET sku=:sku WHERE id=:id");\n            $stmt->bindParam(':id', $id);\n            $stmt->bindParam(':sku', $sku);\n\n            $stmt->execute();\n\n            echo "Updated row with ID: $id and SKU: $sku\n";\n        }\n    }\n\n    // Close the CSV file\n    fclose($handle);\n\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}\n?>