<?php\n// Start session securely\nfunction startSession() {\n    ini_set('session.use_only_cookies', 1);\n    $cookieParams = session_get_cookie_params();\n    session_set_cookie_params(0, $cookieParams['path'], $cookieParams['domain'], true, true); // HTTPS only, HttpOnly flag\n\n    if (empty($_SESSION)) {\n        # regenerate session id for security reasons\n        session_regenerate_id(true);\n    }\n}\n\n// Ensure to set GC lifetime to match your needs\nini_set('session.gc_maxlifetime', 1800); // 30 minutes\nsession_name("SecureSession"); // Custom session name if needed\nstartSession();