// Create post object\n$my_post = array(\n    'post_title'    => wp_strip_all_tags('Your Post Title'),\n    'post_content'  => 'This is your post content.',\n    'post_status'   => 'publish',\n    'post_author'   => 1,\n    'post_category' => array(8) // Use the category ID you want to associate with\n);\n\n// Insert the post into the database\n$post_id = wp_insert_post($my_post);\n\nif (!is_wp_error($post_id)) {\n    echo "Post created with ID: " . $post_id;\n} else {\n    echo "Error creating post.";\n}