function getBoundQuery($query, $params) {\n    // Initialize a counter to keep track of parameter positions\n    $counter = 0;\n    \n    // Use preg_replace_callback to replace ? with corresponding parameters\n    return preg_replace_callback(\n        '/\?/',\n        function ($matches) use (&$params, &$counter) {\n            // Fetch the next parameter value\n            $value = is_numeric($params[$counter]) ? $params[$counter] : "'" . addslashes($params[$counter]) . "'";\n            $counter++;\n            return $value;\n        },\n        $query\n    );\n}\n\n$query = 'SELECT * FROM users WHERE username = ?';\n$params[] = 'bob';\n\n$result = $dbc->prepare($query);\n$result->bindParam(1, $username);\n\n// Get the expanded query string\n$expandedQuery = getBoundQuery($query, $params);\necho $expandedQuery;