<?php\n\n// Database configuration\n$host = 'localhost';\n$dbname = 'your_database_name';\n$username = 'your_username';\n$password = 'your_password';\n\ntry {\n    // Create a new PDO instance for MySQL connection\n    $pdo = new PDO("mysql:host=$host;dbname=$dbname", $username, $password);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // URL of the RSS feed\n    $rssFeedUrl = 'http://example.com/rss.xml';\n\n    // Fetch and parse the RSS feed\n    $rssContent = file_get_contents($rssFeedUrl);\n    $rssData = simplexml_load_string($rssContent);\n\n    // Check if parsing was successful\n    if ($rssData) {\n        foreach ($rssData->channel->item as $item) {\n            // Extract necessary data from each item\n            $title = $pdo->quote((string)$item->title);\n            $link = $pdo->quote((string)$item->link);\n            $description = $pdo->quote((string)$item->description);\n            $pubDate = $pdo->quote(date('Y-m-d H:i:s', strtotime((string)$item->pubDate)));\n\n            // Prepare and execute the SQL statement\n            $stmt = $pdo->prepare("INSERT INTO rss_items (title, link, description, pub_date) VALUES ($title, $link, $description, $pubDate)");\n            $stmt->execute();\n        }\n\n        echo "RSS feed items inserted successfully.";\n    } else {\n        echo "Failed to parse RSS feed.";\n    }\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}\n\n?>