class MessageType {\n    const HOT = 'MSG_HOT';\n    const WARM = 'MSG_WARM';\n    const COLD = 'MSG_COLD';\n\n    public static function isValid($type) {\n        return in_array($type, [self::HOT, self::WARM, self::COLD]);\n    }\n}\n\n// Usage\n$type = MessageType::HOT;\n\nif (MessageType::isValid($type)) {\n    echo "Valid message type.";\n} else {\n    echo "Invalid message type.";\n}