<?php\n// Start output buffering\nob_start();\n\n// Dynamically generate the URL with query parameter\n$queryParams = ['id' => '1249642977'];\n$url = "report.php?" . http_build_query($queryParams);\n\n// Include the file that generates HTML\ninclude_once $url;\n\n// Grab the contents of the output buffer and clean the buffer\n$htmlContent = ob_get_clean();\n\n// Initialize DOMPDF\nrequire_once("dompdf/autoload.inc.php"); // Use the correct path to dompdf autoloader\n\n$dompdf = new Dompdf\Dompdf();\n$dompdf->loadHtml($htmlContent);\n\n// Setup rendering options (optional)\n$dompdf->setPaper('A4', 'portrait');\n\n// Render HTML as PDF\n$dompdf->render();\n\n// Output the generated PDF to Browser\n$dompdf->stream("sample.pdf", ['Attachment' => 0]);\n\n?>