session_start();\n\n// Check if the form is submitted and both username and password are provided\nif (isset($_POST['username']) && isset($_POST['password'])) {\n    $username = $_POST['username'];\n    $password = $_POST['password'];\n\n    // Authenticate user\n    $authed = auth($username, $password);\n    if ($authed) { // Assuming auth() returns true on successful authentication\n        $_SESSION['username'] = $username; // Store only the username in session\n        $navbar = "1";\n        $logindisplay = "0";\n    } else {\n        header('Location: http://website.com/fail.php');\n        exit;\n    }\n} elseif (isset($_SESSION['username'])) { // Check if session is already set\n    $navbar = "1";\n    $logindisplay = "0";\n    $username = $_SESSION['username'];\n} else {\n    header('Location: http://website.com/fail.php');\n    exit;\n}