<?php\n// URL of the XML feed.\n$feed = 'test.xml';\n// Check our XML file exists\nif(!file_exists($feed)) {\n    die('The XML file could not be found!');\n}\n// First, open the XML file.\n$xml = simplexml_load_file($feed);\n// Set the counter for counting how many items we've displayed.\n$counter = 0;\n// Start the loop to display each item.\nforeach($xml->post as $post) {\n    echo '<div style="float:left; width: 180px; margin-top:20px; margin-bottom:10px;">\n            <!-- image file -->\n            <div class="design-sample-txt">'. htmlspecialchars($post->author).'</div>\n          </div>';\n\n    // Increase the counter by one.\n    $counter++;\n    // Check to display an image every 3rd post\n    if($counter % 3 == 0) {\n        echo '<img src="image-source.jpg" alt="Image every 3 posts">'; // Replace with your image source\n    }\n}\n?>