class Template {\n    private $model;\n\n    public function __construct($model) {\n        $this->model = $model;\n    }\n\n    public function getModel() {\n        return $this->model;\n    }\n\n    private function special_include_parse($path, $model) {\n        // Set the model as a property of this object\n        $this->model = $model;\n\n        // Start output buffering to capture the contents of the included file\n        ob_start();\n        \n        // Include the template file\n        include($path);\n\n        // Get the buffered content and clean the buffer\n        return ob_get_clean();\n    }\n\n    public function parse() {\n        return $this->special_include_parse('/var/www/template.html.php', $this->model);\n    }\n}