$conn = oci_connect('username', 'password', '//localhost/XE');\n\nif (!$conn) {\n    $e = oci_error();\n    trigger_error(htmlentities($e['message'], ENT_QUOTES), E_USER_ERROR);\n}\n\n$sql = "INSERT INTO example_table (id, large_text_column) VALUES (:id, :text)";\n\n$stid = oci_parse($conn, $sql);\n\n$id = 1;\n$text = "Your very long text data here...";\n\noci_bind_by_name($stid, ':id', $id);\noci_bind_by_name($stid, ':text', $text, -1, OCI_B_CLOB); // Use -1 for CLOBs\n\n$r = oci_execute($stid, OCI_DEFAULT);\n\nif ($r) {\n    oci_commit($conn);\n} else {\n    $e = oci_error($stid);\n    trigger_error(htmlentities($e['message'], ENT_QUOTES), E_USER_ERROR);\n}\n\noci_free_statement($stid);\noci_close($conn);