function updateUser($table, $id) {\n    if ($_SERVER['REQUEST_METHOD'] === 'POST') {\n        processUpdate($table, $id);\n    } else {\n        updateForm($table, $id);\n    }\n}\n\nfunction processUpdate($table, $id) {\n    // Create a new PDO instance\n    try {\n        $pdo = new PDO('mysql:host=your_host;dbname=your_dbname;charset=utf8', 'username', 'password');\n        $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n        // Prepare and bind\n        $stmt = $pdo->prepare("UPDATE `$table` SET `email`=:email, `lname`=:lname, `fname`=:fname WHERE `id`=:id");\n        $stmt->bindParam(':email', $_POST['email']);\n        $stmt->bindParam(':lname', $_POST['lname']);\n        $stmt->bindParam(':fname', $_POST['fname']);\n        $stmt->bindParam(':id', $id);\n\n        // Execute the statement\n        $stmt->execute();\n\n    } catch (PDOException $e) {\n        echo "Error: " . $e->getMessage();\n    }\n}\n\nfunction updateForm($table, $id) {\n    echo '<form method="post" action="?mode=upd&id=' . htmlspecialchars($id) . '&table=' . htmlspecialchars($table) . '">\n            <input type="email" name="email" required>\n            <input type="text" name="lname" required>\n            <input type="text" name="fname" required>\n            <button type="submit">Update</button>\n          </form>';\n}