<?php\n\n// index.php\n\n// Start session and include necessary files\nsession_start();\ninclude 'routes.php'; // File where routes are defined\n\n// Get the URL path from the query string if it exists\nif (isset($_GET['url'])) {\n    $url = explode('/', $_GET['url']);\n} else {\n    $url[0] = 'home'; // Default route\n}\n\n// Dispatch the request to the appropriate controller/action\ndispatch($routes, $url);\n\nfunction dispatch(array $routes, array $url) {\n    // Check if the requested route exists in the routes array\n    if (isset($routes[$url[0]]) && callable($routes[$url[0]])) {\n        // Call the specified function with any parameters\n        call_user_func_array($routes[$url[0]], array_slice($url, 1));\n    } else {\n        header("HTTP/1.0 404 Not Found");\n        echo "404 Not Found";\n    }\n}