<?php\n\nclass Test {\n    function saveProperty($db) {\n        // Assuming you have a prepared query here to prevent SQL injection\n        $sql = "INSERT INTO your_table (column1, column2) VALUES ('value1', 'value2')"; \n\n        if ($db->query($sql)) {\n            echo "<h3>Data was saved</h3>";\n        } else {\n            echo "<h3>Error saving data: " . $db->error . "</h3>";\n        }\n    }\n}\n\n $db = new mysqli('localhost', 'root', '', 'aqaria');\n if ($db->connect_error) {\n     die("Connection failed: " . $db->connect_error);\n }\n\n$property = new Test();\n$property->saveProperty($db);\n\n$db->close();\n\n?>