session_start();\nrequire_once('core.php');\n\n$mailId = $_GET['id'] ?? null;\nif (isset($mailId)) {\n    $mail = array_filter($_SESSION['inbox'], function($item) use ($mailId) {\n        return $item['id'] === $mailId;\n    });\n\n    if (!empty($mail)) {\n        // Assuming the first item is the mail we want to show\n        echo "<pre>" . htmlspecialchars(print_r(reset($mail), true)) . "</pre>";\n    } else {\n        echo "Mail not found.";\n    }\n} else {\n    echo "No mail ID provided.";\n}