<?php\n\n// Your list of pages\n$list = ['page1', 'page2', 'page3'];\n\n// Function to get the next item in the array\nfunction getNextItem(&$list, &$currentIndex) {\n    if ($currentIndex < count($list) - 1) {\n        $currentIndex++;\n    }\n    return $list[$currentIndex];\n}\n\n// Function to get the previous item in the array\nfunction getPrevItem(&$list, &$currentIndex) {\n    if ($currentIndex > 0) {\n        $currentIndex--;\n    }\n    return $list[$currentIndex];\n}\n\n// Assume you are starting on 'page2' which is index 1\n$currentIndex = 1;\n\n// Let's display the current page\necho "Current Page: " . $list[$currentIndex] . "\n";\n\n// Navigate to the next page\necho "Next Page: " . getNextItem($list, $currentIndex) . "\n"; // Outputs page3\n\n// Navigate back to the previous page\necho "Previous Page: " . getPrevItem($list, $currentIndex) . "\n"; // Outputs page2\n\n?>