function generateShortParams($params) {\n    // Convert array to string: "param1=value1&param2=value2..."\n    $queryString = http_build_query($params);\n    \n    // Encode the query string with Base64 to shorten it\n    return base64_encode($queryString);\n}\n\nfunction parseShortParams($shortParams) {\n    // Decode Base64 encoded string back to the original query string format\n    $queryString = base64_decode($shortParams);\n    \n    // Parse the query string into an array\n    parse_str($queryString, $params);\n    return $params;\n}\n\n// Example usage\n$params = [\n    'param1' => 'value1',\n    'param2' => 'value2',\n    'param3' => 'value3',\n    'param4' => 'value4',\n    'param5' => 'value5'\n];\n\n$shortParams = generateShortParams($params);\necho "Encoded: $shortParams\n"; // This is your shortened parameter string\n\n$decodedParams = parseShortParams($shortParams);\nprint_r($decodedParams); // Should output the original array of parameters