<?php\n$category = $_GET['category'] ?? 'default';\n$images = [];\n\n// Logic to fetch images based on the $category\nswitch ($category) {\n    case 'nature':\n        $images[] = ['src' => 'path/to/nature1.jpg', 'alt' => 'Nature 1'];\n        break;\n    case 'abstract':\n        $images[] = ['src' => 'path/to/abstract1.jpg', 'alt' => 'Abstract 1'];\n        break;\n    default:\n        // Default images\n}\n\nheader('Content-Type: application/json');\necho json_encode($images);\n?>