<?php\n\nrequire 'vendor/autoload.php';\n\nuse Cron\CronExpression;\n\nfunction isValidCron($cron)\n{\n    try {\n        return CronExpression::factory($cron) !== null;\n    } catch (\InvalidArgumentException $e) {\n        return false;\n    }\n}\n\n$cron = "*/30 * * * *";\nif (isValidCron($cron)) {\n    echo "The cron expression is valid.";\n} else {\n    echo "The cron expression is invalid.";\n}