function generatePagination($currentPage, $totalPages, $adjacent = 2) {\n    if ($totalPages < 1) return '';\n\n    $output = '';\n    \n    if ($currentPage > 1) {\n        $output .= '<a href="?page=1">First</a> ';\n    }\n    \n    // Determine the starting and ending points for displaying pages\n    $start = max($currentPage - $adjacent, 1);\n    $end   = min($currentPage + $adjacent, $totalPages);\n    \n    if ($start > 1) {\n        $output .= '... ';\n    }\n\n    // Output the page numbers\n    for ($i = $start; $i <= $end; $i++) {\n        if ($i == $currentPage) {\n            $output .= "[$i] ";\n        } else {\n            $output .= '<a href="?page=' . $i . '">' . $i . '</a> ';\n        }\n    }\n\n    if ($end < $totalPages) {\n        $output .= '... ';\n    }\n    \n    if ($currentPage < $totalPages) {\n        $output .= '<a href="?page=' . $totalPages . '">Last</a>';\n    }\n\n    return trim($output, ' ');\n}\n\n// Example usage\necho generatePagination(5, 10);