<?php\n\nfunction xor_string($input, $key) {\n    $output = '';\n    $key_length = strlen($key);\n    \n    for ($i = 0; $i < strlen($input); $i++) {\n        // XOR each character with the corresponding key byte and store the result in the output string\n        $output .= chr(ord($input[$i]) ^ ord($key[$i % $key_length]));\n    }\n    \n    return $output;\n}\n\nfunction encrypt_decrypt($text, $key) {\n    return xor_string($text, $key);\n}\n\n// Example usage:\n$key = "mySuperSecretKey"; // This key can be your composite of Primary key info + uneditable fields\n$data = "fBJKg}.jkjWfF78dlg@45kjg";\n\n$encrypted_data = encrypt_decrypt($data, $key);\necho "Encrypted: " . bin2hex($encrypted_data) . "\n"; // Using hex to print non-printable characters\n\n// To decrypt\n$decrypted_data = encrypt_decrypt($encrypted_data, $key);\necho "Decrypted: " . $decrypted_data . "\n";\n\n?>