<?php\n\nrequire 'vendor/autoload.php';\n\nuse GeoIp2\Database\Reader;\n\n// Create a new reader instance\n$reader = new Reader('/path/to/database/GeoLite2-Country.mmdb');\n\nfunction getUserCountry($ip) {\n    global $reader;\n    try {\n        // Retrieve the country from your database for the IP address\n        $record = $reader->country($ip);\n        return $record->country->isoCode;\n    } catch (Exception $e) {\n        // Handle the exception if something goes wrong\n        error_log($e->getMessage());\n    }\n    return null;\n}\n\nfunction isAllowedCountry($countryIsoCode, $allowedCountries) {\n    return in_array($countryIsoCode, $allowedCountries);\n}\n\n$ip = $_SERVER['REMOTE_ADDR'];\n$userCountry = getUserCountry($ip);\n\n// Define countries you want to allow\n$allowedCountries = ['US', 'CA'];\n\nif ($userCountry && !isAllowedCountry($userCountry, $allowedCountries)) {\n    http_response_code(403);\n    die('Access denied');\n}\n\n// Your website content here\n\n?>