// Using bindParam\n$stmt = $pdo->prepare("SELECT * FROM users WHERE id = :id");\n$id = 1;\n$stmt->bindParam(':id', $id);\n$stmt->execute();\n$results = $stmt->fetchAll(); // results for user with id = 1\n\n$id = 2;\n$stmt->execute();\n$results = $stmt->fetchAll(); // now results for user with id = 2\n\n// Using bindValue\n$stmt = $pdo->prepare("SELECT * FROM users WHERE id = :id");\n$id = 1;\n$stmt->bindValue(':id', $id);\n$stmt->execute();\n$results = $stmt->fetchAll(); // results for user with id = 1\n\n$id = 2;\n$stmt->execute();\n$results = $stmt->fetchAll(); // still results for user with id = 1, not 2