function sanitizeInput($data, $allowedTags = '') {\n    // Trim whitespace from the beginning and end of string\n    $data = trim($data);\n\n    // Remove potentially dangerous characters (if not using prepared statements for SQL)\n    $data = stripslashes(strip_tags($data));\n\n    // Prevent XSS by converting special HTML characters to entities\n    $data = htmlspecialchars($data, ENT_QUOTES, 'UTF-8');\n\n    // If specific tags are allowed, use strip_tags with the second parameter\n    if (!empty($allowedTags)) {\n        $data = strip_tags($data, $allowedTags);\n    }\n\n    return $data;\n}\n\n// Example usage:\n$userInput = "<script>alert('xss');</script> Hello, <b>World</b>!";\n$safeOutput = sanitizeInput($userInput, '<b><i>');\necho $safeOutput; // Outputs: Hello, <b>World</b>!