<?php\nclass GuitarsController extends AppController {\n    var $name = 'Guitars';\n\n    function index() {\n        $this->set('Guitars', $this->Guitar->findAll());\n        $this->pageTitle = "All Guitars";\n    }\n\n    function view($id = null) {\n        if (!$guitar = $this->Guitar->findById($id)) {\n            // Handle the case when the guitar is not found, e.g., throw an error or redirect\n            $this->Session->setFlash('The requested guitar could not be found.');\n            $this->redirect(array('action' => 'index'));\n        }\n\n        $guitarName = $guitar['Guitar']['Name']; // Accessing the Name attribute\n        $this->set('guitar', $guitar);\n        $this->pageTitle = h($guitarName); // Set the page title and sanitize it for HTML output\n    }\n}\n?>