#!/opt/lampp/bin/php -q \n<?php\nini_set('html_errors', false);\nini_set('implicit_flush', true);\nini_set('max_execution_time', 0);\n\nif ($argc > 1) {\n    $return = explode(' ', trim($argv[1]));\n    if (isset($return[0])) {\n        // Simple replacement logic\n        $url = str_replace("video.yahoo.com", "youtube.com", $return[0]);\n\n        fwrite(STDOUT, $url . PHP_EOL);\n    }\n}\n\nexit(0);