$allowedDomains = ['mylink.com', 'trusted-domain.com']; // List your trusted domains here\n\n// Get the redirected URL from the parameter\n$redirectPage = $_GET['RedirectPage'];\n\n// Parse the URL\n$parsedUrl = parse_url($redirectPage);\n\nif (isset($parsedUrl['host']) && in_array($parsedUrl['host'], $allowedDomains)) {\n    // Redirect to the specified URL\n    header('Location: ' . $redirectPage);\n    exit();\n} else {\n    // Handle the error or redirect to a safe default location\n    header('Location: http://www.myOtherServer.com/error.php');\n    exit();\n}