<?php\n   // Remove expired sessions\n   $timeout = 15 * 60; // 15 minutes\n   $stmt = $pdo->prepare("DELETE FROM user_sessions WHERE last_activity < NOW() - INTERVAL $timeout SECOND");\n   $stmt->execute();\n\n   // Get active users\n   $stmt = $pdo->query("SELECT DISTINCT user_id FROM user_sessions");\n   $active_users = $stmt->fetchAll(PDO::FETCH_COLUMN);\n   \n   echo "Active Users: " . implode(", ", $active_users);\n   ?>