<?php\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    $bmiSubmitted = isset($_POST['bmiSubmitted']);\n    if ($bmiSubmitted) {\n        $height = $_POST['height'];\n        $weight = $_POST['weight'];\n        $formType = $_POST['formType'];\n\n        if ($formType === 'metric') {\n            // Calculate BMI using metric units\n            $bmi = floor($weight / ($height * $height));\n        } elseif ($formType === 'imperial') {\n            // Convert to metric and then calculate BMI\n            $weightInKg = $weight * 0.453592;\n            $heightInMeters = $height * 0.0254;\n            $bmi = floor($weightInKg / ($heightInMeters * $heightInMeters));\n        }\n\n        ?>\n        <ul id="bmi">\n            <li>Weight is: <span><?php echo "$weight"; ?></span></li>\n            <li>Height is: <span><?php echo "$height"; ?></span></li>\n            <li>Body mass index (BMI) is: <span><?php echo "$bmi"; ?></span></li>\n        </ul>\n        <?php\n    }\n} else {\n    // Display forms as you have it in your initial code\n}\n?>