$backupFile = escapeshellarg("backup 2009-04-15 09-57-13.sql");\n$password = escapeshellarg('password');\n$user = 'username';\n$dbname = 'dbname';\n\n$command = "mysqldump --opt -h localhost -u {$user} -p{$password} {$dbname} > {$backupFile}";\n\nexec($command, $output, $return_var);\n\nif ($return_var !== 0) {\n    echo "Error executing command: " . implode("\n", $output);\n} else {\n    echo "Backup successful.";\n}