<?php\n// Database connection\n$mysqli = new mysqli("host", "username", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die('Connection failed: ' . $mysqli->connect_error);\n}\n\n// Your MySQL query\n$query = "SELECT column1, column2, column3 FROM your_table";\n$result = $mysqli->query($query);\n\n// Set headers to download as CSV file\nheader('Content-Type: text/csv');\nheader('Content-Disposition: attachment; filename="data.csv"');\n\n$csvOutput = fopen('php://output', 'w');\n\n// Get the field names from the result set and write them to the CSV\n$fields = $result->fetch_fields();\n$fieldNames = array_map(function($field) {\n    return $field->name;\n}, $fields);\nfputcsv($csvOutput, $fieldNames);\n\n// Write data rows to the CSV\nwhile ($row = $result->fetch_row()) {\n    fputcsv($csvOutput, $row);\n}\n\nfclose($csvOutput);\n\n$mysqli->close();\n?>