<?php\n// Set the current month and year\n$month = date('m');\n$year = date('Y');\n\n// Get the number of days in the month\n$numDaysInMonth = cal_days_in_month(CAL_GREGORIAN, $month, $year);\n\n// Get the first day of the month\n$firstDayOfMonth = date('N', strtotime("$year-$month-01"));\n\n// Get today's day and month for comparison\n$currentDay = date('d');\n$currentMonth = date('m');\n\n// Initialize row counter for <tr>\n$rowCounter = 0;\n?>\n\n<table border="1">\n    <?php\n    // Start the table with an opening <tr>\n    echo "<tr>";\n\n    // Loop to write out the week header (Sunday - Saturday)\n    $daysOfWeek = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];\n    foreach ($daysOfWeek as $day) {\n        echo "<th>$day</th>";\n    }\n    echo "</tr>";\n\n    // Loop to build the calendar days\n    for ($i = 1; $i <= $numDaysInMonth + $firstDayOfMonth - 1; $i++) {\n        if (($i >= $firstDayOfMonth) || $rowCounter != 0) {\n            // Determine the day number\n            $dayNumber = ($i - ($firstDayOfMonth - 1));\n            // Determine class for current day\n            $class = ($currentMonth == $month && $dayNumber == $currentDay) ? 'current-day' : 'row-calendar-cell';\n\n            echo "<td id='$class'>$dayNumber</td>";\n        } else {\n            echo "<td></td>"; // Fill with empty cells before first day of the month\n        }\n\n        // Check to see if we are at the end of a week (7 days), and if so, close row and start new one\n        if (($i - $firstDayOfMonth + 1) % 7 == 0) {\n            echo "</tr><tr>";\n            $rowCounter++;\n        }\n    }\n\n    // Fill remaining cells in last row with blank spaces\n    while(($i - $firstDayOfMonth + 1) % 7 != 1){\n        echo "<td></td>";\n        $i++;\n    }\n\n    // Close the last opened <tr> tag if it was not closed yet\n    echo "</tr>";\n    ?>\n</table>\n\n<style>\n.current-day {\n    background-color: white;\n    font-weight: bold;\n}\n.row-calendar-cell {\n    /* Add custom styles for non-current days here */\n}\n</style>