<?php\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    $search_field = trim($_POST['search_field']);\n    $search_type = $_POST['search_type']; // This will be 'audio' or 'video'\n    $service = $_POST[$search_type]; // This will be the selected service\n\n    // Define URL patterns\n    $url_patterns = [\n        'deezer_audio' => 'http://www.deezer.com/s.php?s=%s',\n        'jiwa_audio'   => 'http://www.jiwa.fm/#search/track/{%22q%22:%22%s%22}',\n        'lastfm_audio' => 'http://www.last.fm/music?q=%s',\n        'youtube_video' => 'http://www.youtube.com/results?search_type=&search_query=%s',\n        'dailymotion_video' => 'http://www.dailymotion.com/relevance/search/%s',\n        'googlevideo_video' => 'http://video.google.com/videosearch?q=%s'\n    ];\n\n    // Prepare the search term\n    $url_params = urlencode($search_field); // Use urlencode for better safety\n\n    // Construct the final URL\n    if (isset($url_patterns[$service . '_' . $search_type])) {\n        $url = sprintf($url_patterns[$service . '_' . $search_type], $url_params);\n        header('Location: ' . $url);\n        exit();\n    } else {\n        die('Invalid search service or type');\n    }\n}\n?>