<?php\n\nclass ForwardIndexer {\n    private $documents = [];\n\n    public function __construct($documentPaths) {\n        $this->documents = $documentPaths;\n    }\n\n    public function indexDocuments() {\n        foreach ($this->documents as $uid => $files) {\n            foreach ($files as $fileName) {\n                $filePath = dirname(__FILE__) . "/documents/$uid/" . $fileName; // Adjust path based on your setup\n                if (file_exists($filePath)) {\n                    $words = $this->extractWordsFromFile($filePath);\n                    echo "$uid -> $fileName -> " . implode(',', $words) . "\n";\n                } else {\n                    echo "File not found: $filePath\n";\n                }\n            }\n        }\n    }\n\n    private function extractWordsFromFile($filePath) {\n        $content = file_get_contents($filePath);\n        // Remove HTML tags\n        $textContent = strip_tags($content);\n        // Tokenize the text into words, ignoring punctuation and converting to lowercase\n        preg_match_all('/\b\w+\b/', strtolower($textContent), $matches);\n        return array_unique($matches[0]);\n    }\n}\n\n// Example usage:\n$documents = [\n    'UID1' => ['index.html', 'foo.html'],\n    'UID2' => ['blah.html', 'gah.txt']\n];\n\n$indexer = new ForwardIndexer($documents);\n$indexer->indexDocuments();\n\n?>