$filePath = realpath("../folder/files.php");\n\n// Open the file in binary mode to avoid any processing by PHP or Apache interpreters\n$handle = fopen("php://filter/read=string.rot13/resource=$filePath", "r");\n\nif ($handle) {\n    $contents = '';\n    while (($line = fgets($handle)) !== false) {\n        // Apply rot13 twice to get the original content, effectively doing nothing but reading lines safely\n        $contents .= str_rot13(str_rot13($line));\n    }\n    fclose($handle);\n    echo nl2br(htmlspecialchars($contents)); // Display contents safely\n} else {\n    echo "Failed to open file.";\n}