<?php\nsession_start();\n\nif (isset($_POST['cancel'])) {\n    if (!empty($_SESSION['uploaded_file'])) {\n        unlink($_SESSION['uploaded_file']['tmp_name']); // Delete the temporary file\n        unset($_SESSION['uploaded_file']);\n    }\n    header('Location: overview.php'); // Back to overview\n} elseif (isset($_POST['confirm'])) {\n    if (!empty($_SESSION['uploaded_file'])) {\n        $upload_path = 'uploads/' . $_SESSION['uploaded_file']['name'];\n        move_uploaded_file($_SESSION['uploaded_file']['tmp_name'], $upload_path);\n        unset($_SESSION['uploaded_file']);\n        header('Location: overview.php?status=success'); // Go back to overview with success message\n    }\n} else {\n    if (!empty($_SESSION['uploaded_file'])) {\n        echo 'Are you sure you want to upload this file: ' . $_SESSION['uploaded_file']['name'] . '?<br>';\n?>\n<form method="POST" action="">\n    <button type="submit" name="confirm">Confirm Upload</button>\n    <button type="submit" name="cancel">Cancel and Delete File</button>\n</form>\n<?php\n    } else {\n        header('Location: overview.php'); // Redirect if no file is set, possibly an error.\n    }\n}\n?>