<?php\n// Start output buffering\nob_start();\n\n// Log the IP address of the incoming connection\n$ip = $_SERVER['REMOTE_ADDR'];\n$logFile = '/path/to/your/logfile.txt';\n\n// Write to the log file with timestamp\nfile_put_contents($logFile, date('Y-m-d H:i:s') . " - Connection from: $ip\n", FILE_APPEND | LOCK_EX);\n\n// Buffer and then flush out all buffered output\nob_end_flush();\n?>