function distributeAmount($amount, $chargeCode) {\n    // Define the mapping of charge codes to agent types\n    $agentTypes = [\n        'A' => 'AgentType1',\n        'J' => 'AgentType2',\n        'L' => 'AgentType3',\n        'A2' => 'AgentType2', // Special case for A2 which is also AgentType2\n    ];\n\n    // Find all matches of agent types in the charge code using a regular expression\n    preg_match_all('/(A|J|L|A2)/', $chargeCode, $matches);\n\n    // Get unique agent types involved\n    $uniqueAgents = array_unique(array_map(function($code) use ($agentTypes) {\n        return $agentTypes[$code];\n    }, $matches[0]));\n\n    // Calculate the share for each unique agent type\n    $sharePerAgent = count($uniqueAgents) > 0 ? $amount / count($uniqueAgents) : 0;\n\n    // Output or return the results as needed\n    foreach ($uniqueAgents as $agentType) {\n        echo "Amount for $agentType: " . $sharePerAgent . "\n";\n    }\n}\n\n// Example usage\ndistributeAmount(50, "AJ");