<?php\n\n// Assuming $isFlashSupported is determined via JavaScript and sent to server\n// This could be done via AJAX or by setting a cookie/localStorage\n\n$isFlashSupported = $_GET['flash'] ?? false;\n\nfunction renderFlashContent($page) {\n    // Render Flash content, possibly using a template engine like Twig in Symfony\n    echo "<object type='application/x-shockwave-flash' data='/path/to/your.swf'>\n          <param name='movie' value='/path/to/your.swf'></param>\n          <param name='flashvars' value='page=$page'></param>\n          Alternative content for browsers that don't support Flash\n          </object>";\n}\n\nfunction renderTextContent($page) {\n    // Render alternative text content, possibly fetching data from MySQL\n    $dbConnection = new PDO('mysql:host=localhost;dbname=your_db', 'username', 'password');\n    $stmt = $dbConnection->prepare("SELECT content FROM pages WHERE slug=:slug");\n    $stmt->execute(['slug' => $page]);\n    $content = $stmt->fetchColumn();\n    echo nl2br($content);\n}\n\n$page = !empty($_GET['page']) ? $_GET['page'] : 'home';\n\nif ($isFlashSupported) {\n    renderFlashContent($page);\n} else {\n    renderTextContent($page);\n}\n?>