<?php\n// Assuming a connection is already made to your database.\n$mysqli = new mysqli("localhost", "my_user", "my_password", "world");\n\nif ($mysqli->connect_error) {\n    die('Connect Error (' . $mysqli->connect_errno . ') ' . $mysqli->connect_error);\n}\n\n$query = "SELECT id, title, start_time, end_time, type FROM appointments";\n$result = $mysqli->query($query);\n\n$appointments = [];\nwhile ($row = $result->fetch_assoc()) {\n    $appointments[] = [\n        'id' => $row['id'],\n        'title' => $row['title'],\n        'start' => $row['start_time'],\n        'end' => $row['end_time'],\n        'display' => 'background', // example of customization for different types\n        'backgroundColor' => '#5390ce', // you can assign colors based on types\n    ];\n}\n\nheader('Content-type: application/json');\necho json_encode($appointments);\n$mysqli->close();\n?>