<?php\nfunction loadFileFromUrl($url) {\n    $ch = curl_init();\n    curl_setopt($ch, CURLOPT_URL, $url);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n    $output = curl_exec($ch);\n    if(curl_errno($ch)) {\n        return 'Request Error:' . curl_error($ch);\n    }\n    curl_close($ch);\n    return $output;\n}\n\n$url = "http://example.com/yourfile.xml";\n$xmlContent = loadFileFromUrl($url);\n\n// Using a simple regex to extract <title> contents\npreg_match_all('/<title>(.*?)<\/title>/si', $xmlContent, $matches);\n$titles = $matches[1];\n\nprint_r($titles);\n?>