function searchWikipedia($query) {\n    $client = new Client();\n    $client->setUri('https://en.wikipedia.org/w/api.php');\n    \n    // Set up parameters for searching pages\n    $params = [\n        'action' => 'query',\n        'list' => 'search',\n        'srsearch' => $query,\n        'format' => 'json'\n    ];\n\n    $client->setParameterGet($params);\n    $response = $client->send();\n    \n    if ($response->isSuccess()) {\n        $data = json_decode($response->getBody(), true);\n        return ['results' => isset($data['query']['search']) ? $data['query']['search'] : []];\n    } else {\n        return ['error' => 'Failed to search Wikipedia'];\n    }\n}\n\n// Example usage for handling ambiguity\n$searchResults = searchWikipedia('Stade de France');\nprint_r($searchResults);